export const PackageManagerIcon: React.FC<PackageManagerIconProps> = ({
  packageManager,
  size = 18,
  color,
}) => {
  switch (packageManager) {
    case 'npm':
      return <NpmIcon size={size} />;
    case 'yarn':
      return <YarnIcon size={size} />;
    case 'pnpm':
      return <PnpmIcon size={size} />;
    case 'bun':
      return <BunIcon size={size} />;
    case 'pip':
    case 'pipenv':
      return <PythonIcon size={size} />;
    case 'poetry':
      return <PoetryIcon size={size} />;
    case 'cargo':
      return <RustIcon size={size} />;
    case 'unknown':
    default:
      return <Package size={size} color={color} />;
  }
};

export default PackageManagerIcon;
